INSERT INTO nodes (_index, created, edited, tag, identity, editor_id, place, timing, hits, displays, body) VALUES (760,874677482,874677482,'tanzaniatrace',82,82,64,250,101,787,'Subject: tanzania vs. amin<br>\r\nDate: 26 Jun 1997 (revisited)<br>\r\nLocation: Masaka, Uganda<p>\r\na peaceful bumpy ride down from slopes of Mbarara. we passed a freshly demolished machine-gun riddled chassis of a white rental at the spot we turned back from just last night and realized that our car could have been heaped right there had we not turned back at the last second last night and pondering this abstraction onto the flat shorelands of Lake Victoria to Masaka, where walls tell another story...\r\n');
