INSERT INTO nodes (_index, created, edited, tag, identity, editor_id, place, timing, hits, displays, body) VALUES (836,875230345,875230574,'couples in cars 24',87,87,68,120,14,229,'A romantic location: A motorway service area by night. Far from life. Lonely neon coloured lights. The cold of the night. The clear sky. The far sounds. The movement. Going on without you.');
