INSERT INTO nodes (_index, created, edited, tag, identity, editor_id, place, timing, hits, displays, body) VALUES (1079,890077848,890078059,'phenomenological',115,115,95,130,13,79,'<font color=ccbb11>Mail to therLands, created by <a href=mailto:ggg@well.com>Phenomenological</a>, Sun, 15.Mar 1998<br>\r\nLocation: North West<p>\r\n\nAhead <b>=</b> a choice; A film, A color, A shape, A movie, <b>THIS</b> cinema  . . .<br> \r\nBehind <b>=</b> every choice that\'s ever been made; <b>ANY</b> film, ALL colors, shapes, movies that ever were.<p>\r\n\n<b><i>Fred</i></b> turns to his left, looks at the viewer, lifts up his hand ...<p>\r\n\n[<b>OFF-STAGE</b> sound of thunder]</font>');
INSERT INTO nodes (_index, created, edited, tag, identity, editor_id, place, timing, hits, displays, body) VALUES (1080,890145104,890145139,'ant-like',116,116,96,160,13,52,'<font color=ccbb11>hello industrial zone!<p>\r\nno, only that here like everywhere in double space - cyber and otherwise - we\'re only ants construting heaps that shift and fall and arise.<br>\r\nperforming - even something as banal as a pass or a walk - on your screen, appearing ant-like as a grain or two of roving grit on your screen, commingling databits with our own bits - we\'re specks, we\'Re ants constructing spaces be they dirt houses or data.warehouses.<br>\r\nCU, p02</font>\r\n');
