INSERT INTO nodes (_index, created, edited, tag, identity, editor_id, place, timing, hits, displays, body) VALUES (1091,890326179,890326179,'descartes',117,117,97,170,13,129,'<font color=ccbb11>Date: Mar. 18 1998<br>\r\nSubject: buzz<br>\r\nLocation: out of touch with everyone<p>\r\nif not mistaken Descartes dreamed up space as most now imagine it <i>(outside of here)</i> as he was resting on his bed one afternoon and watched a fly navigate his room. it was THAT fly that showed him a possible model for space with 3 axes - x, y and z. now it\'s history.</font>');
