INSERT INTO nodes (_index, created, edited, tag, identity, editor_id, place, timing, hits, displays, body) VALUES (1261,910954661,910954661,'extro',134,134,115,220,12,18,'<font color=ccbb11>Date: Fri, 13 Nov 1998 11:35:22 +0100<br>\r\nFrom: <a href=mailto:Schiemenowski@king.dom.de>Birgit Schiemenowski</a><br>\r\nLocation: Braunschweig<p>\r\n\r\nIch finde Knstler vorwiegend sehr introvertiert. Daher finde ich diese \r\nvirtuelle Tagebuch-Reise sehr innovativ fr den intellektuellen-knstlerischen \r\nAustausch. Ich finde es sehr anregend, da \r\nes auch wegfhrt von den traditionellen,\r\nknstlerischen Darstellungsformen wie \r\nMalerei, Bildhauerei und Graphik.\r\nModerne Kunst frs 21. Jahrtausend sollte zu den Medien Video, Film, Perfomances und  Raumkonzepte finden: \"Und so fgt sich alles zum Ganzen.\"</font>');
