INSERT INTO nodes (_index, created, edited, tag, identity, editor_id, place, timing, hits, displays, body) VALUES (1338,913665712,913665712,'yoga',146,146,118,220,21,6,'<font color=ccbb11>Date: Sat, 12 Dec 1998 17:57:25<br>\r\nSubject: mail to therLands<p>\r\n\r\nin yoga they say rounded shoulders are a sign of years of suppressed grief, anger, despair. by doing yoga you can release these years of unhappiness. do it daily. be dedicated. tune into your inner self. you will learn that what i say is true. you can unleash it. you can be happy again. you can find inner peace.');
